changeSet.sql:
insert into sec.permission(name, code, parent_code)
select 'Просмотр справочников', 'rdm.read', (select code from sec.permission where code = 'rdm')
where not exists(select 1 from sec.permission where code = 'rdm.read');

delete from sec.role_permission where permission_code in ( select code from sec.permission where code in ('rdm.read', 'rdm.list') );
delete from sec.permission where code in ('rdm.read', 'rdm.list');
insert into sec.permission(name, code, parent_code) select 'Выгрузка справочников', 'rdm.download', (select code from sec.permission where code = 'rdm') where not exists(select 1 from sec.permission where code = 'rdm.download');
