changeSet.sql:
ALTER TABLE rdm_sync.version ADD COLUMN mapping_last_updated TIMESTAMP WITHOUT TIME ZONE;
UPDATE rdm_sync.version v1 SET mapping_last_updated = (SELECT update_dt FROM rdm_sync.version v2 WHERE v1.code = v2.code);
COMMENT ON COLUMN rdm_sync.version.mapping_last_updated IS 'Время последнего изменения маппинга полей справочника';
CREATE FUNCTION mapping_changed() RETURNS TRIGGER AS $$ BEGIN UPDATE rdm_sync.version SET mapping_last_updated = now()::timestamp WHERE (TG_OP = 'INSERT' AND NEW.code = code) -- Вставка OR (TG_OP = 'DELETE' AND OLD.code = code) -- Удаление OR (TG_OP = 'UPDATE' AND NEW.code = code); -- Изменение RETURN NEW; END; $$ LANGUAGE 'plpgsql';
CREATE TRIGGER on_mapping_updated AFTER INSERT OR UPDATE OR DELETE ON rdm_sync.field_mapping FOR EACH ROW EXECUTE PROCEDURE mapping_changed();
